import React, { useState } from "react";



const Click = () =>{
    const[num, setnum] = useState(0)

    const incre = () =>{
        setnum(num + 1);
    }
    const decre = () =>{
        if(num > 0){
            setnum(num - 1);
        }else{
            setnum(0);
        }
    }

    return(
        <>
            <div className="center_div">
                <h1 className="number">{num}</h1>
                <div className="btn_div">
                    <button onClick={incre} className="btn"><i className="fa fa-plus" aria-hidden="true"></i></button>
                    <button onClick={decre} className="btn"><i className="fa fa-trash" aria-hidden="true"></i></button>
                </div>
            </div>
        </>
    );
}

export default Click;
